import React, { useState } from "react";
import { AskQuestions } from "../JSFiles/AskQuestions";
export default function FAQs() {

  const [FAQQuestions, setFAQQuestions] = useState(AskQuestions);
  const [Show, setShow]=useState(false);

  return (
    <div
      className="div-padding white-bg"
      data-aos="fade-right"
      data-aos-easing="ease-in-sine"
    >
      <div className="container">
        <h2 className="div-heading mt-5" data-aos="flip-left">
          FAQs
        </h2>
        <div className="my-lg-5 my-3">
          <div className="my-3 p-1">
            {
				FAQQuestions.map((currElement) => {
					return (
						<div>
			<button onClick={console.log("button clicked")} type="button" className="collapsible fontbolder">
              {currElement.Question}
            </button>
            <div className="FAQContent py-1">
              <p>
                {Show && currElement.Answer}
              </p>
            </div>
						</div>
						
					)
				})
			}
            
          </div>
        </div>
      </div>
    </div>
  );
}
